package NEW;

public class SumofElementsof2DArray {

	private static int i;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int matrix[][]= {{2,3,4},{1,5,2},{2,4,5}};
		int sum=0;
		for (int i=0; i<3; i++);
		for(int j=0; j<3; j++) {
		sum=sum+matrix[i][j];
					
		}
		System.out.println(sum);

	}
	

}
